<button type="submit" name="action" value="cancel" class="btn btn-sm btn-danger float-right" style="cursor: pointer">Cancel</button>
<button type="submit" name="action" value="hold" class="btn btn-sm btn-warning float-right mr-2" style="cursor: pointer">Hold</button>
<button type="submit" name="action" value="pending" class="btn btn-sm btn-info float-right mr-2" style="cursor: pointer">Pending</button>
<button type="submit" name="action" value="delete" class="btn btn-sm btn-danger float-right" style="cursor: pointer">Delete</button>

<input type="button" onclick='selects()' value="Select All" class="btn btn-sm btn-success float-left"/>
<input type="button" onclick='deSelect()' value="Unselect All" class="btn btn-sm btn-secondary float-left" style="margin-left: 10px"/>